standalone.css:
.standalone-header {
	margin: 0;
	position: static;
	width: auto;
	padding: 0;
}

.standalone-header .inline {
	display: inline;
}

.standalone-header .navbar {
	background-color: #3b414d;
	position: relative;
	box-shadow: none;
	margin: 0;
	padding: 0;
	border: none;
	border-radius: 0;
	color: #b4b4b4;
}

.standalone-header .mobile-navigation {
	padding-top: 18px;
	display: none;
}

.standalone-header .mobile-navigation > button {
	border-radius: 4px;
	margin-right: 5px;
}

.standalone-header .mobile-popover {
	display: none;
	top: 59px;
	left: auto;
	right: 0;
}

.standalone-header .mobile-popover > .arrow {
	left: auto !important;
	right: 16px;
}

.standalone-header .mobile-popover .popover-content {
	color: #333;
}

.standalone-header .mobile-popover .popover-content span {
	display: block;
}

.standalone-header .mobile-popover #popover-login-user {
        margin: 5px 0 0;
        font-weight: bold;
}

.standalone-header .mobile-popover #popover-logout-btn {
        font-weight: normal;
        color: #444;
        background: #e8e8e8;
        width: 100%;
        margin-top: 10px;
}

.standalone-header .mobile-popover #popover-logout-btn:hover {
        background: #ccc;
}

.standalone-header .logo {
	display: table;
	float: left;
	padding: 22px 0 23px 0;
	letter-spacing: 0.08em;
}

.standalone-header .logo .btn {
	height: 42px;
	box-shadow: none;
	-webkit-box-shadow: none;
	padding: 4px 18px 5px 18px;
	border-radius: 4px;
	font-size: 16px;
	font-weight: 600;
}

.standalone-header .btn.btn-grey {
	color: #ffffff !important;
	background-color: #4e5661;
	border-color: transparent;
}

.standalone-header .btn-grey:hover,
.standalone-header .btn-grey:focus,
.standalone-header .btn-grey.focus {
	color: #ffffff;
	background-color: #3a3b3f;
	border-color: rgba(0, 0, 0, 0);
}

.standalone-header .logo .fa {
	width: 26px;
	height: 22px;
	display: inline-block;
	background-size: 24px;
	background-repeat: no-repeat;
	background-position: top left;
	background-image: url('data:image/png;base64,');
	top: 4px;
	font-size: 19px;
	position: relative;
	left: -4px;
}

.standalone-header .logo .fa path {
	fill: white;
}

.standalone-header .font-orange {
	color: #f59c00 !important;
}

.standalone-header .font-white {
	color: #ffffff !important;
}

.standalone-header .metanavi {
	min-height: 40px;
	font-size: 13px;
}

.standalone-header .metanavi > .pull-right {
	position: relative;
}

.standalone-header .bg-metanavi {
	display: inline-table;
	padding: 7px;
	background: #4e5661;
	color: #4c4d52;
	margin-left: 1px;
}

.standalone-header .metanavi .language-nav {
	padding-top: 8px;
}

.standalone-header .metanavi .language {
	top: 1px;
	position: relative;
	padding: 0 2px;
	text-decoration: none;
	cursor: default;
}

.standalone-header .metanavi-bodytext {
	vertical-align: middle;
	margin-right: 7px;
	cursor: default;

}

.standalone-header .logged-in-label {
	color: #ccc;
}

.standalone-header .login-name a {
	cursor: pointer;
	color: #fff;
}

.standalone-header .login-name a:hover {
	color: #f59c00;
}

.standalone-header .metanavi .btn {
	padding: 2px 8px;
	margin: 0;
	font-size: 13px;
	min-width: initial;
	border-radius: 3px;
}

.standalone-header .btn-metanav:hover,
.standalone-header .btn-metanav:focus,
.standalone-header .btn-metanav.focus {
	color: #ffffff;
	background-color: #3a3b3f;
	border-color: rgba(0, 0, 0, 0);
}

.standalone-header .btn-metanav {
	color: #ffffff !important;
	background-color: #717880;
	border-color: transparent;
}

.standalone-header .bg-metanavi::before {
	content: " ";
	display: table;
}

.standalone-header .bg-metanavi .flag {
	position: relative;
	top: -2px;
	margin-right: 5px;
}

.standalone-header .powered-by {
	float: right;
	padding-right: 10px;
	margin-top: 13px;
}

.standalone-header .powered-by .powered-text {
	font-size: 13px;
	margin-right: 8px;
	vertical-align: middle;
	cursor: default;
}

.standalone-header .powered-by .powered-logo {
	height: 13px;
}

@media (max-width:767px) {

	.standalone-header .mobile-navigation {
		display: block;
	}

	.standalone-header .metanavi,
	.standalone-header .powered-by {
		display: none;
	}
}

standalone.js:
setTimeout(function(){
	if (api && api.userId >= 0 && api.userName) {
		var userLink = api.userName;
		document.getElementById('learning-login-user').innerHTML = userLink;
		document.getElementById('popover-login-user').innerHTML = userLink;
		document.getElementById('mobile-toggle-nav').onclick = function () {
			var mobilePopover = document.getElementById('mobile-nav-popover');
			if (mobilePopover && mobilePopover.style.display == 'block') {
				mobilePopover.style.display = 'none';
			} else if(mobilePopover) {
				mobilePopover.style.display = 'block';
			}
		};
  }
  if (elo && elo.locale && elo.locale.language != 'de') {
    document.getElementById('learning-login-label').innerHTML = 'Logged in as: ';
    document.getElementById('popover-login-label').innerHTML = 'Logged in as:';
    document.getElementById('community-logout-btn').innerHTML = 'Logout';
    document.getElementById('popover-logout-btn').innerHTML = 'Logout';
  }
  if (elo && elo.helpers && elo.data && elo.session) {
    document.getElementById('community-logout-btn').onclick = communityLogout;
    document.getElementById('popover-logout-btn').onclick = communityLogout;
  }
});

function communityLogout() {
  elo.helpers.Ajax.call({
      method: "GET",
      url: elo.data.server.relativeAppUrl + '/apps/app/' + elo.data.server.appNamespace + '.' + elo.data.server.appId + '/api/ping',
      params: { logout: "true", ticket: elo.session.SessionLight.ticket }
    },
    function (data) {
      elo.session.SessionLight.redirectToGoodbye();
    }, function (e) {
      elo.session.SessionLight.redirectToGoodbye();  
  });
}
